
/**
 * Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product?
 * 
 * The four adjacent digits in the 1000-digit number that have the greatest product are 9 × 9 × 8 × 9 = 5832.
 * 
 * Ruhika Chatterjee
 * 10/25/16
 */
public class RC_LargestProdPE8
{
    public static void main (String args [])
    {
        String adjStrNumbers = "";
        //The length of this number is 1000
        
        int prod = 0; int largest = 0;
        for (int i = 0; i < adjStrNumbers.length(); i++){
            prod = 1;
            for (int b = 0; b < 13 && b+i+1 < 1000; b ++){
                prod = prod * Integer.parseInt (adjStrNumbers.substring(i+b, i+b+1));
            }
            if (prod > largest) largest = prod;
        }
        System.out.println ("The largest product of 13 consecutive numbers is " + largest);
    }
}

// The largest product of 13 consecutive numbers is 2091059712